window.addEventListener("load",(function(){!function(){const e="iPhone 15 Pro",s=[{storage:"128GB",co2e:"12 kg CO2e"},{storage:"256GB",co2e:"24 kg CO2e"},{storage:"512GB",co2e:"48 kg CO2e"},{storage:"1 TB",co2e:"96 kg CO2e"}],n=document.querySelector(".phone-spec-container"),o=s,t=e;n.innerHTML+=`\n      <p class="phone-spec-title eco-bold"><b>${t} Carbon Emissions</b></p>\n    `;let c=o[0];o.forEach((e=>{parseFloat(e.co2e.split(" ")[0])<parseFloat(c.co2e.split(" ")[0])&&(c=e)})),o.forEach((e=>{const s=parseFloat(e.co2e.split(" ")[0]),o=parseFloat(c.co2e.split(" ")[0]),t=(s-o)/o*100,a=e.storage===c.storage;n.innerHTML+=`\n        <div class="details-container fz-16">\n          <div class="flex-center ${a?"most-green":""} cg-4">\n            <p class="eco-bold"><b>${e.storage} </b>&nbsp;</p>\n            ${a?'<img src="../assets/img/most-green-icon.png" class="icon-16" alt="Most eco-friendly option">':`<span class="red-text fz-12">(+${t.toFixed(0)}% emissions)</span>`}\n          </div>\n          <div class="flex-center co2e-data-container pd-8 br-8 cg-4 lexend-reg">\n            <p class="margin-0">${e.co2e}</p>\n            <img src="../assets/img/equivalent-icon.png" class="icon-16" alt="Equivalent to">\n            <div class="flex-center cg-4">\n              <p class="margin-0 grey-text">${(.88*s).toFixed(1)} kg of trash burned</p>\n              <img src="../assets/img/fire-grey-icon.png" class="icon-16" alt="Trash">\n            </div>\n          </div>\n        </div>\n        `}))}()}));